import React, { useEffect, useMemo, useState } from 'react';
import axios from 'axios';

const rendertable = (data) => {
    return (
        <table className="table table-bordered
        ">
        <thead>
        <tr>
            <th>Id</th>
            <th>Title</th>
            <th>Totaldocs</th>
            <th>Country</th>
            <th>Publisher</th>
            <th>Year</th>
        </tr>
        </thead>
        <tbody>
        {data.map((data, index) =>(
        <tr key={data.id}>
          <td>{data.id}</td>
          <td>{data.title}</td>
          <td>{data.totalDocs}</td>
          <td>{data.country}</td>
          <td>{data.publisher}</td>
          <td>{data.year}</td>
        </tr>
    ))}
        </tbody>
    </table>
    );
}

function Table() {
    const [journals, setjournal] = useState([]);
    const [currentpage, setcurpage] = useState(1);
    const [itemperpage, setitemperpage] = useState(5);
    const [search, setSearch] = useState("");
    const [filterField] = useState(["country","publisher","year"]);

    const [pageLimitNumber, setLimitNumber] = useState(5);
    const [maxPageLimit, setmaxPageLimit] = useState(5);
    const [minPageLimit, setminPageLimit] = useState(0);
    
    const searchange = (e) => {
        setSearch(e.target.value);
    }
    const choosepage = (event) => {
        setcurpage(Number(event.target.id));
    }
    const pages =[];
    for (let i = 1; i <= Math.ceil(journals.length/itemperpage); i++) {
            pages.push(i);
    }

    // const idlastpage =currentpage*itemperpage ;
    // const idfirstpage = idlastpage - itemperpage;
    //const currentitems = journals.slice(currentpage*itemperpage,currentpage*itemperpage - idlastpage);

    const reanderpagenumber = pages.map((nb)=> {
        if (nb < maxPageLimit + 1 && nb > minPageLimit) {
        return (
            <li className= {currentpage == nb? " page-item page-link active" : "page-item page-link"} 
            key = {nb} 
            id = {nb} 
            onClick={choosepage} >
            {nb}
            </li>
        );
        } else {
            return null
        }
    })
    const payload = {"filter":"{}",
        "ignoreAssociation": true,
        "isPaginateDB": true,
        "modelType": "journals",
        "page":1,
        "pageSize":100
      };
    useEffect(() => {
      axios.post('http://o-research-dev.orlab.com.vn/api/v1/filters/filter/', {payload})
        .then(res => {
          setjournal(res.data.data);
        })
        
    }, [])
    const Data = useMemo(() => {
        let data = journals;
        if (search) {
            let lowsearch = search.toLocaleLowerCase();
            data = data.filter(
                journal =>
                    journal.title.toLocaleLowerCase().includes(lowsearch) ||
                    journal.country.toLocaleLowerCase().includes(lowsearch)||
                    journal.publisher.toLocaleLowerCase().includes(lowsearch)||
                    journal.year.toString().toLocaleLowerCase().includes(lowsearch)
            )
        }
        return data.slice(currentpage*itemperpage - itemperpage,currentpage*itemperpage,);
    } , [journals,search,currentpage]);
    const prev = () => {
            setcurpage(currentpage -1);
            if ((currentpage -1) % pageLimitNumber == 0) {
            setmaxPageLimit(maxPageLimit - pageLimitNumber);
            setminPageLimit(minPageLimit - pageLimitNumber);
            }
    }  
    const next = () => {
        document.getElementById("next").classList.remove("disabled"); 
        setcurpage(currentpage + 1);
        if ((currentpage + 1) > maxPageLimit) {
            setmaxPageLimit(maxPageLimit + pageLimitNumber);
            setminPageLimit(minPageLimit + pageLimitNumber);
        }
    }
    
    let pageIncrementBtn = null;
    if (pages.length > maxPageLimit) {
    pageIncrementBtn = <li onClick={next}> &hellip; </li>;
  }

    let pageDecrementBtn = null;
    if (minPageLimit >= 1) {
    pageDecrementBtn = <li onClick={prev}> &hellip; </li>;
  }

    console.log(minPageLimit + "," + maxPageLimit)
    return (
        <div>
            <input
            type="text"
            className="form-control"
            style={{ width: "240px" }}
            placeholder="Search"
            value={search}
            onChange={searchange}
            />
            {rendertable(Data)}
            <ul className="pagination">
                <li >
                <button type="button" className="btn btn-primary" onClick={prev} id = "prev" disabled={currentpage == pages[0]? true : false}>Previous</button>
                </li>

                {pageDecrementBtn}
                {reanderpagenumber}
                {pageIncrementBtn}
                
                <li >
                <button type="button" className="btn btn-primary" onClick={next} id = "next" disabled={currentpage == pages[pages.length -1]? true : false}>Next</button>
                </li>
            </ul>
        </div>
      );
    
}

export default Table;
