import React, { useState } from 'react';

const item= (id, ids) => {
    const[val, setval] = useState("hide");

    const display = () => {
        const checkbox_val = document.getElementById(id).value;
        console.log(checkbox_val);
        const class_cell = id + "_col";
        if (val == "hide") {
            const all_col =   document.getElementsByClassName(class_cell);
            for (let i = 0; i < all_col.length; i++) {
                all_col[i].style.display = "none";
            }
            document.getElementById(id+"_head").style.display="none";
            setval("show");            
        } else  {
            const all_col =   document.getElementsByClassName(class_cell);
            for (let i = 0; i < all_col.length; i++) {
                all_col[i].style.display = "table-cell";
            }
            document.getElementById(id+"_head").style.display="table-cell";
            setval("hide");
        }
    }
    return (
        <input type="checkbox" val ={val} key={ids} id= {id} onChange ={display()} />
    );
}

const  Display = ({header})=> {
    const [hide, setval] = useState([{col: "",hide: ""}]);
    
    return (
        <div className="btn-group dropright">
            <button type="button" className="btn btn-secondary dropdown-toggle" data-toggle="dropdown" aria-haspopup="true" aria-expanded="false">
                Custom display
            </button>
            <ul className="dropdown-menu">
                {header.map(({name, field, ids})=> (
                    <li><item id = {field} ids = {ids} />{name}</li>
                ))
                }
            </ul>
        </div>
    );
}


export default Display;
