import React, { Component } from 'react';
import axios from 'axios';

class Table extends Component {
    constructor() {
        super();
        this.state = {
            journals: []
        }
    }
    
    componentDidMount() {
      const payload = {"filter":"{}",
        "ignoreAssociation": true,
        "isPaginateDB": true,
        "modelType": "journals",
        "page":1,
        "pageSize":100
      };
      var journals = this.state.journals;
      axios.post('http://o-research-dev.orlab.com.vn/api/v1/filters/filter/', {payload})
        .then(res => {
          console.log(res);
          console.log(res.data);
          this.state.journals = res.data.data;
          this.setState(this.state);
        })
    }
  
    renderJournals = () => {
        let journals = this.state.journals.map((data, index) =>
            <tr key={data.id}>
              <td>{data.id}</td>
              <td>{data.title}</td>
              <td>{data.totalDocs}</td>
              <td>{data.country}</td>
              <td>{data.publisher}</td>
              <td>{data.year}</td>
            </tr>
        );
  
        return journals;
    }
  
    render() {
      return (
        <table className="table">
            <thead>
            <tr>
                <th>Id</th>
                <th>Title</th>
                <th>Totaldocs</th>
                <th>Country</th>
                <th>Publisher</th>
                <th>Year</th>
            </tr>
            </thead>
            <tbody>
            {this.renderJournals()}
            </tbody>
        </table>
      );
    }
}

export default Table;
