import React, {useState} from "react";

const Tableheader = ({headers, onSort}) => {

    return (
        <thead>
        <tr>
            {headers.map(({name, field})=> (
                <th 
                key = {name}
                onClick={()=> onChangeSort(field)}    
                >
                {name}
                </th>
            ))}
            
        </tr>
        </thead>
    );
}
