import React, { useEffect, useState } from 'react';
import axios from 'axios';

function Table() {
    const [journals, setjournal] = useState([]);
    const [currentpage, setcurpage] = useState(1);
    const [itemperpage, setitemperpage] = useState(5);

    const [pageLimitNumber, setLimitNumber] = useState(5);
    const [maxPageLimit, setmaxPageLimit] = useState(5);
    const [minPageLimit, setminPageLimit] = useState(0);
    const pages =[];
    for (let i = 1; i <= Math.ceil(journals.length/itemperpage); i++) {
            pages.push(i);
    }
    const choosepage = (event) => {
        setcurpage(Number(event.target.id))
    }
    const prev = () => {
        if (currentpage == pages[0]) {
            document.getElementById("prev").classList.add("disabled");
        } else {
            let curpage = currentpage -1; 
            setcurpage(curpage);
            if (curpage % pageLimitNumber == 0) {
            setmaxPageLimit(maxPageLimit - pageLimitNumber);
            setminPageLimit(minPageLimit - pageLimitNumber);
            }
        }
    }
    const next = () => {
        if (currentpage == pages[pages.length -1]) {
            document.getElementById("prev").classList.add("disabled");
        } else {
        let curpage = currentpage + 1; 
        setcurpage(curpage);
        if (curpage % pageLimitNumber == 0) {
            setmaxPageLimit(maxPageLimit + pageLimitNumber);
            setminPageLimit(minPageLimit + pageLimitNumber);
        }
    }
    }
    const idlastpage = currentpage*itemperpage;
    const idfirstpage = idlastpage - itemperpage;
    const currentitems = journals.slice(idfirstpage,idlastpage);
    console.log(currentitems);
    const reanderpagenumber = pages.map((nb)=> {
        if (nb <= maxPageLimit && nb > minPageLimit) {
        return (
            <li className= {currentpage == nb? " page-item page-link active" : "page-item page-link"} 
            key = {nb} 
            id = {nb} 
            onClick={choosepage} >
            {nb}
            </li>
        );
        } else {
            return null
        }
    })
    
    const rendertable = (data) => {
        return (
            <table className="table">
            <thead>
            <tr>
                <th>Id</th>
                <th>Title</th>
                <th>Totaldocs</th>
                <th>Country</th>
                <th>Publisher</th>
                <th>Year</th>
            </tr>
            </thead>
            <tbody>
            {data.map((data, index) =>(
            <tr key={data.id}>
              <td>{data.id}</td>
              <td>{data.title}</td>
              <td>{data.totalDocs}</td>
              <td>{data.country}</td>
              <td>{data.publisher}</td>
              <td>{data.year}</td>
            </tr>
        ))}
            </tbody>
        </table>
        );
    }
    const payload = {"filter":"{}",
        "ignoreAssociation": true,
        "isPaginateDB": true,
        "modelType": "journals",
        "page":1,
        "pageSize":100
      };
    useEffect(() => {
      axios.post('http://o-research-dev.orlab.com.vn/api/v1/filters/filter/', {payload})
        .then(res => {
          setjournal(res.data.data);
        })
        
    }, [])
    console.log(idfirstpage + "," +idlastpage)
    return (
        <div>
            {rendertable(currentitems)}
            <ul className="pagination">
                <li className="page-item page-link" onClick={prev} id = "prev">
                Previous
                </li>
                {reanderpagenumber}
                <li className="page-item page-link" onClick={next} id = "next">
                Next
                </li>
            </ul>
        </div>
      );
    
}

export default Table;
