import React, { useEffect, useState } from 'react';
import axios from 'axios';

function Table() {
    const [journals, setjournal] = useState([]);
    const [currentpage, setcurpage] = useState(1);
    const [itemperpage, setitemperpage] = useState(5);

    const pages =[];
    for (let i = 1; i <= Math.ceil(journals.length/itemperpage); i++) {
            pages.push(i);
    }
    const idlastpage = currentpage*itemperpage;
    const idfirstpage = idlastpage - itemperpage;
    const currentitems = journals.slice(idfirstpage,idlastpage);
    console.log(currentitems);
    const reanderpagenumber = pages.map((nb)=> {
        return (
            <li class="page-item" onClick={choosepage} key = {nb} id = {nb}><a class="page-link" >{nb}</a></li>
        );
    })
    const choosepage = (event) => {
        setcurpage(Number(event.target.id))
    }
    const rendertable = (data) => {
        return (
            <table className="table">
            <thead>
            <tr>
                <th>Id</th>
                <th>Title</th>
                <th>Totaldocs</th>
                <th>Country</th>
                <th>Publisher</th>
                <th>Year</th>
            </tr>
            </thead>
            <tbody>
            {data.map((data, index) =>(
            <tr key={data.id}>
              <td>{data.id}</td>
              <td>{data.title}</td>
              <td>{data.totalDocs}</td>
              <td>{data.country}</td>
              <td>{data.publisher}</td>
              <td>{data.year}</td>
            </tr>
        ))}
            </tbody>
        </table>
        );
    }
    const payload = {"filter":"{}",
        "ignoreAssociation": true,
        "isPaginateDB": true,
        "modelType": "journals",
        "page":1,
        "pageSize":100
      };
    useEffect(() => {
      axios.post('http://o-research-dev.orlab.com.vn/api/v1/filters/filter/', {payload})
        .then(res => {
          setjournal(res.data.data);
        })
        
    }, [])
    console.log(idfirstpage + "," +idlastpage)
    return (
        <div>
            {rendertable(currentitems)}
            <ul class="pagination">
                <li class="page-item disabled">
                <a class="page-link" href="#" tabindex="-1">Previous</a>
                </li>
                {reanderpagenumber}
                <li class="page-item">
                <a class="page-link" href="#">Next</a>
                </li>
            </ul>
        </div>
      );
    
}

export default Table;
