import React from 'react';

const  Display = ({header})=> {
    
    const display = (col_name) => {
        const checkbox_val = document.getElementById(col_name).value;
        const class_cell = col_name + "_col";
        if (checkbox_val == "hide") {
            const all_col =   document.getElementsByClassName(class_cell);
            for (let i = 0; i < all_col.length; i++) {
                all_col[i].style.display = "none";
            }
            document.getElementById(col_name+"_head").style.display="none";
            document.getElementById(col_name).value="show";
        } else {
            const all_col =   document.getElementsByClassName(class_cell);
            for (let i = 0; i < all_col.length; i++) {
                all_col[i].style.display = "table-cell";
            }
            document.getElementById(col_name+"_head").style.display="table-cell";
            document.getElementById(col_name).value="hide";
        }
    }
    return (
        <div className="btn-group dropright">
            <button type="button" className="btn btn-secondary dropdown-toggle" data-toggle="dropdown" aria-haspopup="true" aria-expanded="false">
                Custom display
            </button>
            <div className="dropdown">
                <ul className='dropdown-menu'>
                {header.map(({name, field})=> (
                    <li><input type="checkbox" key={name} value ="hide" id= {field} onChange ={e => display(e.target.id)} />{name}</li>
                ))
                }
                </ul>
            </div>
        </div>
    );
}


export default Display;
